import React, { useState, useEffect ,useContext} from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";

const BookingChart = () => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [pendingCount, setPendingCount] = useState(null);
  const [successfulCount, setSuccessfulCount] = useState(null);
  const { token } = useContext(AuthContext);
  useEffect(() => {
    axios
    .get("http://localhost:8888/api/bookings/count-pending", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
    .then((response) => {
      const pendingCount = response.data;
      setPendingCount(pendingCount);
      return axios.get("http://localhost:8888/api/bookings/count-successful", {
        params: {
          pendingCount,
        },
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
    })
    .then((response) => {
      const successfulCount = response.data;
      setSuccessfulCount(successfulCount);
      return axios.get("http://localhost:8888/api/bookings/count-cancelled", {
        params: {
          successfulCount,
        },
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
    })
    .then((response) => {
      const cancelledCount = response.data;
      setLoading(false);
      setData([
        { status: "Pending", count: pendingCount },
        { status: "Successful", count: successfulCount }, 
        { status: "Cancelled", count: cancelledCount },
      ]);
    })
    .catch((error) => {
    
      setLoading(false);
    });
  
  }, [pendingCount, successfulCount]);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <BarChart width={600} height={400} data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="status" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="count" fill="rgba(75, 192, 192, 0.2)" />
        </BarChart>
      )}
    </div>
  );
};

export default BookingChart;
