import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";
import { Container, Row, Col } from "reactstrap";

const BookingChart = () => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const { token } = useContext(AuthContext);

  useEffect(() => {
    axios
      .get("http://localhost:8888/bookings/count-by-status-and-month", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        const countsByStatusAndMonth = response.data;

        // Chuyển đổi dữ liệu nhận được thành định dạng phù hợp cho biểu đồ
        const chartData = [];

        // Lặp qua các tháng và trạng thái
        countsByStatusAndMonth.forEach((item) => {
          // item là một đối tượng với các thuộc tính: status, count, month
          chartData.push({
            status: item.status,
            month: item.month,
            count: item.count,
          });
        });

        setData(chartData);
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
      });
  }, [token]);

  return (
    <Container>
      <Row className="justify-content-center mt-4">
        <Col xs="12" sm="8" md="6">
          {loading ? (
            <p>Loading...</p>
          ) : (
            <BarChart width={600} height={400} data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="count" fill="red" name="Số lượng" />
            </BarChart>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default BookingChart;
