import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";
import { Container, Row, Col } from "reactstrap";
const BookingChart = () => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const { token } = useContext(AuthContext);

  useEffect(() => {
    axios
      .get("http://localhost:8888/api/bookings/by-status", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        const bookings = response.data;

        // Tạo đối tượng để lưu trữ số lượng booking theo từng status
        const statusCounts = {
          PENDING: 0,
          SUCCESSFUL: 0,
          FAILED: 0,
        };

        // Đếm số lượng booking theo từng status
        bookings.forEach((booking) => {
          statusCounts[booking.status] += 1;
        });

       
        const chartData = Object.keys(statusCounts).map((status) => ({
          status,
          count: statusCounts[status],
        }));

        setData(chartData);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });
  }, [token]);

  return (
    <Container>
      <Row className="justify-content-center mt-4">
        <Col xs="12" sm="8" md="6">
          {loading ? (
            <p>Loading...</p>
          ) : (
            <BarChart width={600} height={400} data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="revenue" fill="red" name="Doanh thu" />
            </BarChart>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default BookingChart;
