import React, { useState, useEffect, useContext } from "react";
import { Bar } from 'react-chartjs-2';
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";

const MonthlyRevenueChart = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const { token } = useContext(AuthContext);

  useEffect(() => {
    axios
      .get("http://localhost:8888/bookings/total-amount-by-month", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setData(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });
  }, [token]);

  useEffect(() => {
    if (!loading && data.length > 0) {
      // Đảm bảo dữ liệu đã sẵn sàng trước khi render biểu đồ
      renderChart();
    }
  }, [loading, data]);

  const renderChart = () => {
    const chartData = {
      labels: ['Tháng 12', 'Tháng 11', 'Tháng 10', 'Tháng 9', 'Tháng 8', 'Tháng 7', 'Tháng 6', 'Tháng 5', 'Tháng 4', 'Tháng 3', 'Tháng 2', 'Tháng 1'],
      datasets: [
        {
          label: 'Số tiền (VND)',
          data: data.reverse(),
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
      ],
    };

    const chartOptions = {
      scales: {
        y: {
          beginAtZero: true,
        },
      },
    };

    return (
      <div>
        <Bar data={chartData} options={chartOptions} />
      </div>
    );
  };

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        renderChart()
      )}
    </div>
  );
};

export default MonthlyRevenueChart;
