import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";

const BookingChart = () => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const { token } = useContext(AuthContext);

  useEffect(() => {
    axios
      .get("http://localhost:8888/api/bookings/by-status", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        const bookings = response.data;

        // Tạo đối tượng để lưu trữ số lượng booking theo từng status
        const statusCounts = {
          PENDING: 0,
          SUCCESSFUL: 0,
          FAILED: 0,
        };

        // Đếm số lượng booking theo từng status
        bookings.forEach((booking) => {
          statusCounts[booking.status] += 1;
        });

        // Chuyển đổi đối tượng statusCounts thành mảng data cho biểu đồ
        const chartData = Object.keys(statusCounts).map((status) => ({
          status,
          count: statusCounts[status],
        }));

        setData(chartData);
        setLoading(false);
      })
      .catch((error) => {
        console.error(error);
        setLoading(false);
      });
  }, [token]);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <BarChart width={600} height={400} data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="status" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="count" fill="rgba(75, 192, 192, 0.2)" />
        </BarChart>
      )}
    </div>
  );
};

export default BookingChart;
