import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import { Container, Row, Col, Form, FormGroup, Label, Input, Spinner, Table } from "reactstrap";
import MonthlyRevenueChart from './MonthlyRevenueChart';

const BookingStats = () => {
  const [month, setMonth] = useState(1);
  const [totalAmount, setTotalAmount] = useState("");
  const [loading, setLoading] = useState(false);
  const { token } = useContext(AuthContext);
  const baseUrl = "http://localhost:8888/bookings";

  const fetchTotalAmount = () => {
    setLoading(true);
    axios
      .get(`${baseUrl}/total-amount-by-month`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setTotalAmount(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchTotalAmount();
  }, [month]);

  return (
    <Container>
      <h2 className="mt-4">Thống kê số tiền booking thành công trong tháng</h2>
      <Form>
        <FormGroup>
          <Label for="selectMonth">Chọn tháng:</Label>
          <Input
            type="select"
            name="select"
            id="selectMonth"
            value={month}
            onChange={(e) => setMonth(e.target.value)}
          >
            <option value="1">Tháng 1</option>
            <option value="2">Tháng 2</option>
            <option value="3">Tháng 3</option>
            <option value="4">Tháng 4</option>
            <option value="5">Tháng 5</option>
            <option value="6">Tháng 6</option>
            <option value="7">Tháng 7</option>
            <option value="8">Tháng 8</option>
            <option value="9">Tháng 9</option>
            <option value="10">Tháng 10</option>
            <option value="11">Tháng 11</option>
            <option value="12">Tháng 12</option>
          </Input>
        </FormGroup>
      </Form>
      {loading ? (
        <Spinner color="primary" />
      ) : (
        <div>
          <Table>
            <thead>
              <tr>
                <th>Tháng</th>
                <th>Tổng số tiền (VND)</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Tháng {month}</td>
                <td>{totalAmount}</td>
              </tr>
            </tbody>
          </Table>
          <MonthlyRevenueChart data={totalAmount} />
        </div>
      )}
    </Container>
  );
};

export default BookingStats;
