import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../../context/AuthContext";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";

const BookingChart = () => {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [pendingCount, setPendingCount] = useState(null);
  const [successfulCount, setSuccessfulCount] = useState(null);
  const { token } = useContext(AuthContext);

  useEffect(() => {
    axios
      .get("http://localhost:8888/api/bookings/count-pending", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        const pendingCount = response.data;
        setPendingCount(pendingCount);
        return axios.get("http://localhost:8888/api/bookings/count-successful", {
          params: {
            pendingCount,
          },
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
      })
      .then((response) => {
        const successfulCount = response.data;
        setSuccessfulCount(successfulCount);
        return axios.get("http://localhost:8888/api/bookings/count-cancelled", {
          params: {
            successfulCount,
          },
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
      })
      .then((response) => {
        const cancelledCount = response.data;
        setLoading(false);
        setData([
          { status: "Pending", count: pendingCount, fill: "rgba(255, 0, 0, 0.6)" },
          { status: "Successful", count: successfulCount, fill: "rgba(0, 255, 0, 0.6)" },
          { status: "Cancelled", count: cancelledCount, fill: "rgba(0, 0, 255, 0.6)" },
        ]);
      })
      .catch((error) => {
        setLoading(false);
      });
  }, [pendingCount, successfulCount]);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <BarChart width={600} height={400} data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="status" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="count" fill="fill" />
        </BarChart>
      )}
    </div>
  );
};

export default BookingChart;
